
public class If_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 300;
		
		switch (30)
		{
		case (10):
		System.out.println(a);
		break;
		case (20):
		System.out.println(b);
		break;
		case(30):
		System.out.println(c);	
		break;
		
	}
	}

}
